mapper.update:
update au_user
user_name=#{userName}, user_password=#{userPassword}, real_name=#{realName}, user_age=#{userAge}, user_gender=#{userGender}, last_login_time=#{lastLoginTime}
where id = #{id}

mapper.select:
select id, user_name, user_password, real_name, user_age, user_gender, create_time, last_login_time
from au_user
where user_name = #{userName}
